mapper.select:
SELECT * from
realName like CONCAT('%',#{condition},'%') or `name` like CONCAT('%',#{condition},'%') or sex like CONCAT('%',#{condition},'%') or identityCode like CONCAT('%',#{condition},'%') or email like CONCAT('%',#{condition},'%') or mobile like CONCAT('%',#{condition},'%')
limit #{startIndex},#{pageSize}
